INSERT INTO [dbo].[Task] ([Id], [idLev], [Level], [Condition], [Code]) VALUES (1, 1, 0, N'Написать программу, которая подсчитывает периметр квадрата, площадь которого равна a.', N'using System;using System.Collections.Generic;using System.Linq;using System.Text.RegularExpressions;namespace Rextester{public class Program{public static void Main(string[] args){int a;a=int.Parse(Console.ReadLine()); double b=(Math.Sqrt(a))*4;Console.WriteLine(b);}}}')
INSERT INTO [dbo].[Task] ([Id], [idLev], [Level], [Condition], [Code]) VALUES (2, 1, 0, N'Написать программу, которая определяет наибольшую цифру в натуральном двухзначном числе.', N'using System;using System.Collections.Generic;using System.Linq;using System.Text.RegularExpressions;namespace Rextester{public class Program{public static void Main(string[] args){int a;int b;int c;a = int.Parse(Console.ReadLine());b = a / 10;c = a % 10;if (b > c) Console.WriteLine("Первая цифра больше");else Console.WriteLine("Вторая цифра больше");}}}')
INSERT INTO [dbo].[Task] ([Id], [idLev], [Level], [Condition], [Code]) VALUES (3, 1, 0, N'Написать программу, которая подсчитывает среднее арифметическое кубов двух данных чисел.', N'using System;using System.Collections.Generic;using System.Linq;using System.Text.RegularExpressions;namespace Rextester{public class Program{public static void Main(string[] args){double c1 = double.Parse(Console.ReadLine());;double c2 = double.Parse(Console.ReadLine());Console.Write((Math.Pow(c1, 3) + Math.Pow(c2, 3)) / 2);}}}')
INSERT INTO [dbo].[Task] ([Id], [idLev], [Level], [Condition], [Code]) VALUES (1, 2, 1, N'Написать программу, которая определяет, является ли заданное целое число нечетным.', N'using System;using System.Collections.Generic;using System.Linq;using System.Text.RegularExpressions;namespace Rextester{public class Program{public static void Main(string[] args){int c = int.Parse(Console.ReadLine());if (c % 2 == 0) Console.WriteLine("Число четное");else Console.WriteLine("Число нечетное");}}}')
INSERT INTO [dbo].[Task] ([Id], [idLev], [Level], [Condition], [Code]) VALUES (2, 2, 2, N'Написать программу, которая определяет день недели по номеру.', N'using System;using System.Collections.Generic;using System.Linq;using System.Text.RegularExpressions;namespace Rextester{public class Program{public static void Main(string[] args){int numberOfWeek;numberOfWeek = int.Parse(Console.ReadLine());switch (numberOfWeek){case 1: Console.WriteLine("Понедельник"); break;case 2: Console.WriteLine("Вторник"); break;case 3: Console.WriteLine("Среда"); break;case 4: Console.WriteLine("Четверг"); break;case 5: Console.WriteLine("Пятница"); break;case 6: Console.WriteLine("Суббота"); break;case 7: Console.WriteLine("Воскресение"); break; default: Console.WriteLine("Такого дня не существует"); break;}}}} ')
INSERT INTO [dbo].[Task] ([Id], [idLev], [Level], [Condition], [Code]) VALUES (3, 2, 3, N'Вывести на экран целые числа 1, 3, 5, …, 21  в строчку через пробел.', N'using System;using System.Collections.Generic;using System.Linq;using System.Text.RegularExpressions;namespace Rextester{public class Program{public static void Main(string[] args){byte n = byte.Parse(Console.ReadLine());int i = 1;while (i <= n){Console.Write(" " + i);i += 2;}}} ')